import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name :null

        }
    }

/*    renderSquare = (i) => {
        return(
            <Square
            name = {this.state.name} 
            onClick = {() => this.changeSquare()}
            />
        )

    }*/

    changeSquare = () => {
        this.setState({
            name: 'X'
        })
    }



    render() {
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square id = '1' onClick = {this.changeSquare()}/>
                                <Square id = '2'/>
                                <Square id = '3'/>
                                
                            </ul>
                            <ul>
                                <Square id = '4'/>
                                <Square id = '5'/>
                                <Square id = '6'/>
                            </ul>
                            <ul>
                                <Square id = '7'/>
                                <Square id = '8'/>
                                <Square id = '9'/>
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }
}

export default Board;
