import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name :null

        }
    }

/*    renderSquare = (i) => {
        return(
            <Square
            name = {this.state.name} 
            onClick = {() => this.changeSquare()}
            />
        )

    }*/

    changeSquare = () => {
            this.setState({
                name: 'X'
    })
        
        
    }



    render() {
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square id = '1' onClick = {console.log('OK')}/>
                                <Square id = '2' onClick = {() => this.changeSquare()}/>
                                <Square id = '3' onClick = {() => this.changeSquare()}/>
                                
                            </ul>
                            <ul>
                                <Square id = '4' onClick = {() => this.changeSquare()}/>
                                <Square id = '5' onClick = {() => this.changeSquare()}/>
                                <Square id = '6' onClick = {() => this.changeSquare()}/>
                            </ul>
                            <ul>
                                <Square id = '7' onClick = {() => this.changeSquare()}/>
                                <Square id = '8' onClick = {() => this.changeSquare()}/>
                                <Square id = '9' onClick = {() => this.changeSquare()}/>
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }
}

export default Board;
