import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name :'-'

        }
    }

    renderSquare = (i) => {
        return(
            <Square
            name = {this.state.name} 
            onClick = {() => this.changeSquare()}
            />
        )

    }

    changeSquare = () => {
        this.setState({
            name: 'X'
        })
    }



    render(){
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                {this.renderSquare(1)}
                                {this.renderSquare(2)}
                                {this.renderSquare(3)}
                            </ul>
                            <ul>
                                {this.renderSquare(4)}
                                {this.renderSquare(5)}
                                {this.renderSquare(6)}
                            </ul>
                            <ul>
                                {this.renderSquare(7)}
                                {this.renderSquare(8)}
                                {this.renderSquare(9)}
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }
}

export default Board;
