import React from "react";
import Square from "./Square";
import Winner from "./Winner";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name : Array(9).fill('-'),
            xIsNext: true,

        }
    }

    
   changeSquare(i) {
    const square = this.state.name;
   
    if(Winner(square)) {        
        return;
    }else {
        
        square[i] = this.state.xIsNext ? 'X' : 'O';
        this.setState({
          name: square,
          xIsNext: !this.state.xIsNext,
          });

    }

    

    }



    render() {
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square name = {this.state.name[0]} onClick = {() => this.changeSquare(0)} />
                                <Square name = {this.state.name[1]} onClick = {() => this.changeSquare(1)} />
                                <Square name = {this.state.name[2]} onClick = {() => this.changeSquare(2)} />
                                
                            </ul>
                            <ul>
                                <Square name ={this.state.name[3]} onClick = {() => this.changeSquare(3)} />
                                <Square name ={this.state.name[4]} onClick = {() => this.changeSquare(4)} />
                                <Square name ={this.state.name[5]} onClick = {() => this.changeSquare(5)} />
                            </ul>
                            <ul>
                                <Square name ={this.state.name[6]} onClick = {() => this.changeSquare(6)} />
                                <Square name ={this.state.name[7]} onClick = {() => this.changeSquare(7)} />
                                <Square name ={this.state.name[8]} onClick = {() => this.changeSquare(8)} />
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }

    
}



export default Board;
