import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name : Array(9).fill(null),
            xIsNext: true,

        }
    }

    
   changeSquare(i) {
    const square = this.state.name.slice();
    if(Winner(square) || square[i] ) {
      return;
    }
    square[i] = this.state.xIsNext ? 'X' : 'O';
    this.setState({
      name: square,
      xIsNext: !this.state.xIsNext,
      });
    
    
    }



    render() {
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square id = '0' name = {this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '1' name = {this.state.name[this.props.id]} osnClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '2' name = {this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                
                            </ul>
                            <ul>
                                <Square id = '3' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '4' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '5' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                            </ul>
                            <ul>
                                <Square id = '6' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '7' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                                <Square id = '8' name ={this.state.name[this.props.id]} onClick = {() => this.changeSquare(this.props.id)} />
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }

    
}

function Winner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8],
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8],
      [0, 4, 8],
      [2, 4, 6],
    ];
    for (let i = 0; i < lines.length; i++) {
      const [a, b, c] = lines[i];
      if ( squares[a] === squares[b] && squares[a] === squares[c]) {
        return squares[a];
      }
    }
    return null;
  }

export default Board;
