import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name :null

        }
    }
   changeSquare(id) {
       if (this.props.id !== id ) {
           return
       }
        this.setState({name: 'X'});
    
    }



    render() {
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square id = '1'  onClick = {() => this.changeSquare(1)} name ={this.state.name} />
                                <Square id = '2'  onClick = {() => this.changeSquare(2)} name ={this.state.name}/>
                                <Square id = '3'  onClick = {() => this.changeSquare(3)} name ={this.state.name}/>
                                
                            </ul>
                            <ul>
                                <Square id = '4' onClick = {() => this.changeSquare(4)} name ={this.state.name}/>
                                <Square id = '5' onClick = {() => this.changeSquare(5)} name ={this.state.name}/>
                                <Square id = '6' onClick = {() => this.changeSquare(6)} name ={this.state.name}/>
                            </ul>
                            <ul>
                                <Square id = '7' onClick = {() => this.changeSquare(7)} name ={this.state.name}/>
                                <Square id = '8' onClick = {() => this.changeSquare(8)} name ={this.state.name}/>
                                <Square id = '9' onClick = {() => this.changeSquare(9)} name ={this.state.name}/>
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }
}

export default Board;
