import React from "react";
import Square from "./Square";
import '../style/Board.css'


class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name :'-'

        }
    }

    changeSquare = () => {
        this.setState({
            name: 'X'
        })
    }



    render(){
        return(
                <div>

                        <div className="board">
                            
                            <ul>
                                <Square name = {this.state.name} onClick = {() => changeSquare()} />
                                <Square name = {this.state.name} />
                                <Square name = {this.state.name} />
                            </ul>
                            <ul>
                                <Square name = {this.state.name} />
                                <Square name = {this.state.name} />
                                <Square name = {this.state.name} />
                            </ul>
                            <ul>
                                <Square name = {this.state.name} />
                                <Square name = {this.state.name} />
                                <Square name = {this.state.name} />
                            </ul>

                         </div>              
                     
                </div>
                         
        )
    }
}

export default Board;
